function HeroSection() {
  const { useRef, useMemo } = React;
  const { Canvas, useFrame } = ReactThreeFiber;
  const { OrbitControls, Environment } = Drei;

  const vertexShader = `
uniform float uTime;
uniform vec2 uMouse;
varying vec2 vUv;
varying vec3 vPosition;
void main() {
  vUv = uv;
  vPosition = position;
  vec3 pos = position;
  float wave = sin(pos.x * 2.0 + uTime) * cos(pos.y * 2.0 + uTime) * 0.1;
  float mouseInfluence = length(uMouse - pos.xy) * 0.5;
  pos.z += wave + sin(mouseInfluence + uTime) * 0.05;
  gl_Position = projectionMatrix * modelViewMatrix * vec4(pos, 1.0);
}`;

  const fragmentShader = `
uniform float uTime;
uniform vec2 uMouse;
uniform vec3 uColor1;
uniform vec3 uColor2;
uniform vec3 uColor3;
varying vec2 vUv;
varying vec3 vPosition;
void main() {
  vec2 uv = vUv;
  float gradient1 = sin(uv.x * 3.0 + uTime * 0.5) * cos(uv.y * 3.0 + uTime * 0.3);
  float gradient2 = sin(uv.x * 5.0 - uTime * 0.7) * cos(uv.y * 2.0 + uTime * 0.4);
  vec2 mouseUv = uMouse * 0.5 + 0.5;
  float mouseDistance = distance(uv, mouseUv);
  float mouseEffect = 1.0 - smoothstep(0.0, 0.5, mouseDistance);
  float mixFactor = (gradient1 + gradient2) * 0.5 + 0.5 + mouseEffect * 0.3;
  vec3 color = mix(uColor1, uColor2, mixFactor);
  color = mix(color, uColor3, sin(uTime * 0.5) * 0.5 + 0.5);
  float noise = fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
  color += noise * 0.05;
  gl_FragColor = vec4(color, 0.8);
}`;

  const CustomShaderMaterial = () => {
    const materialRef = useRef();
    const uniforms = useMemo(() => ({
      uTime: { value: 0 },
      uMouse: { value: [0,0] },
      uColor1: { value: [0.082,0.502,0.239] },
      uColor2: { value: [0.518,0.8,0.086] },
      uColor3: { value: [0.941,1.0,0.957] },
    }), []);

    useFrame(state => {
      if(materialRef.current){
        materialRef.current.uniforms.uTime.value = state.clock.elapsedTime;
        materialRef.current.uniforms.uMouse.value = [
          (state.mouse.x * state.viewport.width)/2,
          (state.mouse.y * state.viewport.height)/2
        ];
      }
    });

    return <shaderMaterial ref={materialRef} vertexShader={vertexShader} fragmentShader={fragmentShader} uniforms={uniforms} transparent />;
  };

  const AnimatedMesh = () => (
    <mesh rotation={[0,0,0]} scale={[4,3,1]}>
      <planeGeometry args={[2,2,32,32]} />
      <CustomShaderMaterial />
    </mesh>
  );

  const FloatingParticles = () => {
    const particles = Array.from({ length: 50 }, (_,i) => (
      <mesh key={i} position={[(Math.random()-0.5)*10,(Math.random()-0.5)*10,(Math.random()-0.5)*5]} scale={Math.random()*0.1+0.05}>
        <sphereGeometry args={[1,8,8]} />
        <meshBasicMaterial color="#84cc16" transparent opacity={0.6} />
      </mesh>
    ));
    return <group>{particles}</group>;
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <div className="absolute inset-0 w-full h-screen">
        <Canvas camera={{ position:[0,0,5], fov:75 }} style={{ background:'transparent' }}>
          <Environment preset="dawn" />
          <ambientLight intensity={0.5} />
          <pointLight position={[10,10,10]} intensity={1} />
          <AnimatedMesh />
          <FloatingParticles />
          <OrbitControls enableZoom={false} enablePan={false} enableRotate autoRotate autoRotateSpeed={0.5} />
        </Canvas>
      </div>
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen text-center px-4">
        <h1 className="text-5xl md:text-7xl font-bold text-foreground leading-tight">
          Experience the <span className="bg-gradient-to-r from-primary to-accent bg-clip-text text-transparent">Future</span> of Web Technology
        </h1>
      </div>
    </div>
  );
}
 export defaultHeroSection;

